import React from 'react';
import './App.css';
import './custom.scss';
import Navbar from './Components/UI/Navbar';

function App() {
  return (
    <div className="App h-100" style={{ background: `linear-gradient(-225deg, #2c3c94, #2c947b)` }}>
      <div className='container main-content d-flex align-items-center justify-content-center'>
          <div className='shadow h-100 w-75 p-5 bg-light shadow d-flex align-items-center justify-content-center'>
            <div className='container'>

            <h1 className='display-1 fw-bold mb-2'>Covered</h1>
            <hr></hr>
            <h5 className='text-start'>Dear User,</h5>
            <p className='text-start'>I am excited to introduce myself as your new Cover Letter Generator software! I use OpenAI technology to generate high-quality cover letters that are tailored to your specific needs.</p>
            <p className='text-start'>I understand that writing a cover letter can be a daunting task, so I have made it my mission to make the process as simple and straightforward as possible. With my software, you can create a professional and effective cover letter quickly and easily.</p>
            <p className='text-end'>Best regards, Covered</p>
            <hr></hr>
            <button className='btn btn-primary shadow'>Lets save you some time</button>
            </div>
          </div>

      </div>
      <Navbar></Navbar>
    </div>
  );
}

export default App;
