import React, { FormEvent, useRef, useState } from 'react'
import DynamicInput from '../Components/UI/DynamicInput'

export default function SkillsEditor() {

    class Skills {

        skill: string;

        constructor(skill: string) {
            this.skill = skill;
        }
    }
    const [skills, setSkills] = useState(['Leadership']);

    const handleAddButton = () => {
        setSkills([...skills, ''])
    }
    return (
        <>
            <form>

                {
                    skills.map((skill) => {
                        return <DynamicInput />
                    })
                }

                <button type="button" className="btn btn-info" onClick={handleAddButton}>add skill</button>
            </form>
        </>
    )
}
