import React from 'react';
import './App.css';
import './custom.scss';
import Navbar from './Components/UI/Navbar';

function App() {
  return (
    <div className="App h-100" style={{ background: `linear-gradient(-225deg, #2c3c94, #2c947b)` }}>
      <div className='container main-content d-flex align-items-center justify-content-center'>
        <div className='h-100 pt-5'>
          <div className='shadow h-100 w-100 p-5 bg-light shadow'>
            <h1 className='display-1 fw-bold mb-2'>Covered</h1>
            <h5 className='text-start'>To our dear users: </h5>
            <p>I am excited to introduce myself as your new Cover Letter Generator software! I use OpenAI technology to generate high-quality cover letters that are tailored to your specific needs.</p>
            <button className='btn btn-primary shadow'>Lets save you some time</button>
          </div>

        </div>

      </div>
      <Navbar></Navbar>
    </div>
  );
}

export default App;
