import React, { ReactHTML, useState } from 'react'

interface UserData {
  name: string;
  location: string;
  position: string;
  companyName: string;
  education: string;
  schoolName: string;
  extraComments: string;
}
export default function () {
  const [userData, setUserData] = useState<UserData>({
    name: '',
    location: '',
    position: '',
    companyName: '',
    education: '',
    schoolName: '',
    extraComments: ''
  });
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setUserData({ ...userData, [event.target.name]: event.target.value });
  };
  const handleSelect = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setUserData({ ...userData, [event.target.name]: event.target.value });
  }
  return (
    <>
      <form>
        <h1>Let's Make This Quick</h1>
        <label className="w-100 mx-1 mb-1 text-center text-primary fw-normal fs-6">Note: Covered does NOT save any of this information.</label>
        <hr></hr>
        <div className="form-floating mb-3">
          <input type="email" className="form-control" id="floatingInput" name='name' placeholder="name" value={userData.name} onChange={handleChange} />
          <label htmlFor="floatingInput">Your Name</label>
        </div>

        <div className="form-floating mb-3">
          <input type="email" className="form-control" id="floatingInput" name='name' placeholder="name" value={userData.name} onChange={handleChange} />
          <label htmlFor="floatingInput">Where are you from?</label>
        </div>

        <div className="form-floating mb-3">
          <input type="email" className="form-control" id="floatingInput" name='position' placeholder="position" value={userData.position} />
          <label htmlFor="floatingInput">The Position</label>
        </div>

        <div className="form-floating mb-3">
          <input type="email" className="form-control" id="floatingInput" name='companyName' placeholder="company name" value={userData.companyName} />
          <label htmlFor="floatingInput">Company Name</label>
        </div>

        <select className="form-select mb-3" aria-label="Default select example" name='education' value={userData.education} onChange={handleSelect}>
          <option selected hidden value=''>Your Education</option>
          <option value="None">None</option>
          <option value="High School Degree">High School Degree</option>
          <option value="Bachelor's Degree">Bachelor's Degree</option>
          <option value="Master's Degree">Master's Degree</option>
          <option value="PhD">PhD</option>
        </select>

        <div className="form-floating mb-3">
          <input type="email" className="form-control" id="floatingInput" name="schoolName" placeholder="name@example.com" value={userData.schoolName} />
          <label htmlFor="floatingInput">School Name</label>
        </div>

        <label htmlFor="floatingTextarea" className="w-100 mx-1 mb-1 text-start fw-light fs-6">Hint: Add more comments (Why are you exited to apply?) </label>
        <div className="form-floating mb-3">
          <textarea className="form-control h-100" placeholder="Leave a comment here" id="floatingTextarea" name='extraComments' value={userData.extraComments}></textarea>
          <label htmlFor="floatingTextarea">Extra Comments</label>
        </div>

        <button className='btn btn-primary shadow'>Lets save you some time</button>

      </form>
    </>
  )
}
