import React, { FormEvent, useRef, useState } from 'react'
import { Link } from 'react-router-dom';
import { arrayBuffer } from 'stream/consumers';
import { convertToObject } from 'typescript';
import SkillInput from '../Components/UI/SkillInput';
import { Skill } from '../Domain/Skill';
import { useAppDispatch, useAppSelector } from '../Hooks/hooks';
import { setUserData } from '../Slices/FormSlice';

export default function SkillsEditor() {
    //const [skills, setSkills] = useState<string[]>(["test"]);
    const dispatch = useAppDispatch();
    const skills = useAppSelector(state => state.skills);

    const handleAddButton = () => {
        if (skills.length < 5) {
            //setSkills([...skills, ''])
            dispatch(setUserData)
        }
    }
    const handleRemove = (index: number) => {
        if (skills.length > 1) {
            let updatedArray = skills.filter((_, i) => i != index)
            //setSkills(updatedArray)
        }
    }
    const handleChange = (index: number, event: React.ChangeEvent<HTMLInputElement>) => {
        let updatedArray = [...skills];
        updatedArray[index] = event.target.value;
        //setSkills(updatedArray)
    }
    return (
            <form className='w-100 p-1'> 
                <h1 className='text-center'>Tell us about your skills</h1>
                <hr></hr>
                {
                    skills.map((skill, index) => {
                        return <SkillInput
                        skill={skill}
                        index={index}
                        handleChange={handleChange}
                        handleRemove={handleRemove} />
                    })
                }
                {
                    skills.length < 5 ? 
                    <button type="button" className="btn btn-success w-100 my-2 py-1 shadow fs-4" onClick={handleAddButton}>+</button> :
                    <></>
                }
                <hr></hr>
                <Link to={"/summary"} className='btn btn-primary shadow my-2 w-100 p-3'>I'm Done!</Link>
            </form>
    )
}
